screens.custom_css:
/***StyleDiego--HealthSavingsAccoount--07-05-24*****/
[selector='Bg-containerAT'] {
    background-color: #FFFFFF;
}

[selector='bg-container']{
    background-color: #DEEBFF;
}

[selector='bg-form']{
    background-color: #F6F9FB;
    -webkit-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    -moz-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    padding: 10px 30px;
    max-width: 873px;
    margin: auto;
}

[selector='Bg-Form2']{
    background-color: #FFFFFF;
    padding: 10px 40px;
}

[selector='bg-Header']{
    background: linear-gradient(45deg, rgba(188,220,248,1) 0%,rgba(188,220,248,1) 40%,rgba(246,249,251,1) 40%,rgba(246,249,251,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ *//* Permalink - use to edit and share this gradient: https://colorzilla.com/gradient-editor/#bcdcf8+0,bcdcf8+40,f6f9fb+40,f6f9fb+41,0d4779+41,0d4779+41,0d4779+100 */
    background: linear-gradient(45deg, rgba(188,220,248,1) 0%,rgba(188,220,248,1) 40%,rgba(246,249,251,1) 40%,rgba(246,249,251,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    -webkit-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    -moz-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    max-width: 873px;
    margin: auto;
}
[selector='Bg-Header2']{ 
    background: linear-gradient(to right, #bcdcf8 0%, #ffffff 100%);
    /*max-width: 873px;
    margin: auto;*/
}
[selector='Bg-Header3']{ 
    background: linear-gradient(to right, #bcdcf8 0%, #ffffff 100%);
    height: 100px;
}

[selector='Bg-headerList']{ 
    background-color:#95C8F3;
    padding: 10px 15px 0 15px;
}
[selector='Bg-GridList'] .row{ 
    margin-right:0px !important;
    margin-left:0px !important;
}
[selector='Bg-GridList'] .col-sm-3{ 
    border:1px solid #95C8F3;
}

[selector='bg-Footer']{
    background: linear-gradient(45deg, rgba(188,220,248,1) 0%,rgba(188,220,248,1) 40%,rgba(246,249,251,1) 40%,rgba(246,249,251,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ *//* Permalink - use to edit and share this gradient: https://colorzilla.com/gradient-editor/#bcdcf8+0,bcdcf8+40,f6f9fb+40,f6f9fb+41,0d4779+41,0d4779+41,0d4779+100 */
    background: linear-gradient(45deg, rgba(188,220,248,1) 0%,rgba(188,220,248,1) 40%,rgba(246,249,251,1) 40%,rgba(246,249,251,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 41%,rgba(13,71,121,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    -webkit-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    -moz-box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    box-shadow: 10px 10px 4px -6px rgba(179,179,179,1);
    height: 68px;
    max-width: 873px;
    margin: auto;
}
[selector='logo-img']{
    padding: 25px 20px 10px 20px;
}

[selector='logo-img2']{
    padding: 35px 20px 25px 40px;
}

.hdrTxt {
    text-align: right;
    color: #FFFFFF;
    font-size: 20px;
    font-weight: bold;
    letter-spacing: 0.075rem;
    line-height: 1.2;
    padding-right: 10px;
    padding-top: 5px;
}
.hdrTxt2 {
    text-align: right;
    color: #556271;
    font-size: 20px;
    font-weight: bold;
    letter-spacing: 0.075rem;
    line-height: 1.2;
    margin: 0px 50px;
}
.hdrTxt3 {
    text-align: right;
    color: #556271;
    font-size: 20px;
    font-weight: 400;
    letter-spacing: 0.075rem;
    line-height: 1.2;
    margin: 25px 50px 0px 50px;
}
.WhitehdrTxt2 {
    text-align: right;
    color: #FFFFFF;
    font-size: 20px;
    font-weight: bold;
    letter-spacing: 0.075rem;
    line-height: 1.2;
    margin: 0px 20px;
}
.WhitehdrTxt3 {
    text-align: right;
    color: #FFFFFF;
    font-size: 20px;
    font-weight: 400;
    letter-spacing: 0.075rem;
    line-height: 1.2;
    margin: 15px 20px 0px 20px;
}


.Textlitle{
    color: #9dabb6;
    font-size: 17px;
    font-weight: 600;
    line-height: 24px;
}
.TitleHealth{
    color: #343434;
    font-size: 40px;
    font-weight: 700;
    line-height: 45.6px;
}
.TextField{
    color: #556271;
    font-size: 16px;
    font-weight: 400;
    line-height: 24px;
    letter-spacing: 1px;
}
.TextFieldGrid{
    color: #556271;
    font-size: 16px;
    font-weight: 400;
    line-height: 24px;
    padding-top: 12px;
}

.TextFieldWhite{
    color: #FFFFFF;
    font-size: 16px;
    font-weight: 600;
    line-height: 24px;
}
.TitleSeccion{
    color: #0B3961;
    border-bottom: 2px solid #BCDCF8;
    font-size: 19px;
    font-weight: 700;
    line-height: 35px;
}
.TitleSeccion2{
    background-color: #DEEEFA;
    color: #556271;
    font-size: 17px;
    font-weight: 600;
    padding: 10px 20px;
    margin: 20px 0px;
}
.form-group label{
    color: #556271;
    font-size: 15px;
    font-weight: 400;
    line-height: 20.43px;
}
.form-control {
    color: #556271;
    border: 2px solid #bfc4c9;
    border-radius: 4px;
    padding: 10px;
    margin-bottom: 15px
}

[selector='btn-position'] .btn-primary{
    background-color:#1572C2;
    border: 1px solid #1572C2;
    color: #FFFFFF;
    border-radius: 8px;
    width: 100%;
}

/***Approval template***/


[selector='loopText'] {
   text-align: center;
}

.required-asterisk{
    color:#1572C2;
}
[selector='Bg-Approve'] .btn-primary{
    background-color:#4EA075;
    border: 1px solid #4EA075;
    color: #FFFFFF;
    border-radius: 4px;
    width: 100%;
}
[selector='Bg-Reject'] .btn-danger{
    background-color:#ED4858;
    border: 1px solid #ED4858;
    color: #FFFFFF;
    border-radius: 4px;
    width: 100%;
}

